from common import print_tour, read_input, format_tour
import math

def distance(a, b):
    return math.sqrt((a[0] - b[0]) ** 2 + (a[1] - b[1]) ** 2)

def triSquare(a,b,c):
    x1,y1 = a
    x2,y2 = b
    x3,y3 = c
    return abs((x1*y2 - x2*y1) + (x2*y3 - x3*y2) + (x3*y1 - x1*y3)) / 2

def isIntersect(a,b,c,d):
    s = triSquare(a,b,c) + triSquare(a,b,d) - triSquare(a,c,d) - triSquare(b,c,d)
    return True if s == 0 else False

def adjustIntersect(result):
    changed = False
    for i in range(n):
        a, b = tour[result[i]], tour[result[i + 1]]
        for j in range(i + 2, n):
            c, d = tour[result[j]], tour[result[j + 1]]
            if len(set([a,b,c,d])) == 4 and isIntersect(a, b, c, d):
                result = result[:i + 1] + result[i + 1:j + 1][::-1] + result[j + 1:]
                changed = True
    return result, changed

def optimize(result):
    for i in range(n):
        for j in range(1, n):
            if j != i and j != i+1:
                a, b = tour[result[i]], tour[result[i + 1]]
                c, d, e = tour[result[j - 1]], tour[result[j]], tour[result[j + 1]]
                if distance(a, b) + distance(c, d) + distance(d, e) > distance(a, d) + distance(d, b) + distance(c, e):
                    d_id = result.pop(j)
                    if i < j:
                        result.insert(i+1,d_id)
                    else:
                        result.insert(i,d_id)
                    print(i,j)
                    return result, True
    return result, False

